import React, { useState, useRef, useEffect } from 'react';
import './App.css';
import botLogo from './bot.png';
import { WaveFile } from 'wavefile';

function App() {
  // States
  const [isRecording, setIsRecording] = useState(false);
  const [status, setStatus] = useState('Session ready.');
  const [error, setError] = useState('');
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [liveTranscript, setLiveTranscript] = useState('');
  const [messages, setMessages] = useState([]);

  // Refs
  const inputAudioContextRef = useRef(null);
  const outputAudioContextRef = useRef(null);
  const inputNodeRef = useRef(null);
  const outputNodeRef = useRef(null);
  const mediaStreamRef = useRef(null);
  const sourceNodeRef = useRef(null);
  const scriptProcessorNodeRef = useRef(null);
  const nextStartTimeRef = useRef(0);
  const sourcesRef = useRef(new Set());
  const wsRef = useRef(null);
  const isRecordingRef = useRef(false);
  const currentTurnIdRef = useRef(null);
  const lastInterruptTimeRef = useRef(0);

  // Audio context helpers
  const createInputAudioContext = () => {
    const ctx = new (window.AudioContext || window.webkitAudioContext)({ sampleRate: 16000 });
    inputNodeRef.current = ctx.createGain();
    inputAudioContextRef.current = ctx;
    return ctx;
  };

  const createOutputAudioContext = () => {
    const ctx = new (window.AudioContext || window.webkitAudioContext)({ sampleRate: 24000 });
    outputNodeRef.current = ctx.createGain();
    outputNodeRef.current.connect(ctx.destination);
    outputAudioContextRef.current = ctx;
    return ctx;
  };

  const initAudio = () => {
    if (!inputAudioContextRef.current || inputAudioContextRef.current.state === 'closed') createInputAudioContext();
    if (!outputAudioContextRef.current || outputAudioContextRef.current.state === 'closed') createOutputAudioContext();
    nextStartTimeRef.current = outputAudioContextRef.current.currentTime;
  };

  // Convert base64 audio data to ArrayBuffer
  function base64ToArrayBuffer(base64String) {
    try {
      if (typeof base64String !== 'string' || base64String.length === 0) throw new Error('Invalid base64 string input');
      const cleanBase64 = base64String.replace(/^data:[^;]+;base64,/, '').replace(/\s+/g, '');
      const binaryString = atob(cleanBase64);
      const bytes = new Uint8Array(binaryString.length);
      for (let i = 0; i < binaryString.length; i++) bytes[i] = binaryString.charCodeAt(i);
      return bytes.buffer;
    } catch (error) {
      setError(`Audio conversion failed: ${error.message}`);
      throw error;
    }
  }

  // Play decoded audio buffer
  const playAudioBuffer = async (audioBuffer, turnId = null) => {
    if (turnId && turnId !== currentTurnIdRef.current) return;
    const source = outputAudioContextRef.current.createBufferSource();
    source.buffer = audioBuffer;
    source.connect(outputNodeRef.current);
    source.onended = () => { sourcesRef.current.delete(source); };
    const now = outputAudioContextRef.current.currentTime;
    const startTime = Math.max(nextStartTimeRef.current, now + 0.01);
    try {
      source.start(startTime);
      nextStartTimeRef.current = startTime + audioBuffer.duration;
      sourcesRef.current.add(source);
    } catch (error) {
      setError(`Error playing audio: ${error.message}`);
    }
  };

  // Decode raw PCM audio into AudioBuffer
  async function decodeAudioData(arrayBuffer) {
    try {
      const view = new DataView(arrayBuffer);
      const sampleCount = arrayBuffer.byteLength / 2;
      const float32Array = new Float32Array(sampleCount);
      for (let i = 0; i < sampleCount; i++) {
        const int16Sample = view.getInt16(i * 2, true);
        float32Array[i] = int16Sample / (int16Sample < 0 ? 32768 : 32767);
      }
      const audioBuffer = outputAudioContextRef.current.createBuffer(1, sampleCount, 24000);
      audioBuffer.copyToChannel(float32Array, 0);
      return audioBuffer;
    } catch (error) {
      setError(`Audio decode failed: ${error.message}`);
      throw error;
    }
  }

  // Handle interrupt messages from backend
  const handleInterruption = (turnId, timestamp) => {
    const now = Date.now();
    if (now - lastInterruptTimeRef.current < 100) return;
    lastInterruptTimeRef.current = now;
    sourcesRef.current.forEach(source => { try { source.stop(); } catch {} });
    sourcesRef.current.clear();
    nextStartTimeRef.current = outputAudioContextRef.current.currentTime;
    setIsAIGenerating(false);
    currentTurnIdRef.current = null;
  };

  // Connect WebSocket to backend for audio streaming and control messages
  const connectWebSocket = () => {
    const ws = new window.WebSocket(
      window.location.protocol === 'https:'
        ? `wss://${window.location.host}/api/genai-audio`
        : `ws://${window.location.hostname}:5050/api/genai-audio`
    );
    ws.binaryType = 'arraybuffer';
    ws.onopen = () => setStatus('WebSocket connected.');
    ws.onmessage = async (event) => {
      try {
        const msg = JSON.parse(event.data);
        if (msg.type === 'status') setStatus(msg.message);
        else if (msg.type === 'error') setError(msg.message);
        else if (msg.type === 'interrupt') handleInterruption(msg.turnId, msg.timestamp);
        else if (msg.type === 'generation_start') {
          setIsAIGenerating(true);
          currentTurnIdRef.current = msg.turnId;
        }
        else if (msg.type === 'audio' && msg.data) {
          const arrayBuffer = base64ToArrayBuffer(msg.data);
          const audioBuffer = await decodeAudioData(arrayBuffer);
          playAudioBuffer(audioBuffer, currentTurnIdRef.current);
        }
      } catch (err) {
        setError(`Error handling message: ${err.message}`);
      }
    };
    ws.onerror = () => setError('WebSocket error');
    ws.onclose = () => setStatus('WebSocket closed.');
    wsRef.current = ws;
  };

  const closeWebSocket = () => {
    wsRef.current?.close();
    wsRef.current = null;
  };

  // Start recording from microphone
  const startRecording = async () => {
    if (isRecordingRef.current) return;
    initAudio();
    await inputAudioContextRef.current.resume();
    setStatus('Requesting microphone access...');
    setLiveTranscript('');
    setError('');
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaStreamRef.current = mediaStream;
      setStatus('Microphone access granted. Starting capture...');
      connectWebSocket();
      const sourceNode = inputAudioContextRef.current.createMediaStreamSource(mediaStream);
      sourceNodeRef.current = sourceNode;
      sourceNode.connect(inputNodeRef.current);
      const scriptProcessorNode = inputAudioContextRef.current.createScriptProcessor(256, 1, 1);
      scriptProcessorNode.onaudioprocess = (event) => {
        if (!isRecordingRef.current) return;
        const pcmData = event.inputBuffer.getChannelData(0);
        const int16Array = new Int16Array(pcmData.length);
        for (let i = 0; i < pcmData.length; i++) {
          int16Array[i] = Math.max(-32768, Math.min(32767, Math.floor(pcmData[i] * 32767)));
        }
        if (wsRef.current?.readyState === WebSocket.OPEN) wsRef.current.send(int16Array.buffer);
      };
      sourceNode.connect(scriptProcessorNode);
      scriptProcessorNode.connect(inputAudioContextRef.current.destination);
      scriptProcessorNodeRef.current = scriptProcessorNode;
      setIsRecording(true);
      isRecordingRef.current = true;
      setStatus('üî¥ Recording... Capturing PCM chunks.');
    } catch (err) {
      setError(`Error: ${err.message}`);
      setStatus('Error starting recording');
      stopRecording();
    }
  };

  // Stop recording and cleanup resources
  const stopRecording = () => {
    setStatus('Stopping recording...');
    setIsRecording(false);
    isRecordingRef.current = false;
    scriptProcessorNodeRef.current?.disconnect();
    sourceNodeRef.current?.disconnect();
    scriptProcessorNodeRef.current = null;
    sourceNodeRef.current = null;
    mediaStreamRef.current?.getTracks().forEach(track => track.stop());
    mediaStreamRef.current = null;
    sourcesRef.current.forEach(source => { try { source.stop(); } catch {} });
    sourcesRef.current.clear();
    closeWebSocket();
    inputAudioContextRef.current?.close();
    inputAudioContextRef.current = null;
    outputAudioContextRef.current?.close();
    outputAudioContextRef.current = null;
    setStatus('Recording stopped. Click Start to begin again.');
  };

  // Reset everything
  const reset = () => {
    stopRecording();
    setStatus('Session cleared.');
    setMessages([]);
    setLiveTranscript('');
  };

  // Cleanup on unmount
  useEffect(() => {
    initAudio();
    return () => {
      stopRecording();
      inputAudioContextRef.current?.close();
      inputAudioContextRef.current = null;
      outputAudioContextRef.current?.close();
      outputAudioContextRef.current = null;
      closeWebSocket();
    };
  }, []);

return (
  <div className="bot-ui">
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '100%'
    }}>
      <img src={botLogo} alt="Bot" className="bot-avatar" />
    </div>
    <h2 className="bot-title">Revolt AI Voice Bot</h2>
    <div className="bot-intro">
      This bot provides helpful information about <b>Revolt Motors</b> in multiple languages.<br />
      Ask anything about Revolt models, electric bikes, dealerships, charging, and features!
    </div>
    <div className="button-group controls">
      <button className="start-btn" onClick={startRecording} disabled={isRecording}>
        ‚ñ∂Ô∏è Start
      </button>
      <button className="stop-btn" onClick={stopRecording} disabled={!isRecording}>
        ‚èπÔ∏è Stop
      </button>
      <button className="reset-btn" onClick={reset} disabled={isRecording}>
        üîÑ Reset
      </button>
    </div>
    <div className="live-transcript">
      {isRecording && <em>Transcribing... {liveTranscript}</em>}
    </div>
    <div className="chat-window">
      {messages.map(msg => (
        <div key={msg.id} className={`chat-bubble ${msg.sender}`}>
          {msg.text}
        </div>
      ))}
    </div>
    <div className={error ? "status-text error" : "status-text"}>
      {error || status}
    </div>
  </div>
);
}
export default App;
